CREATE OR REPLACE FUNCTION sus_obter_se_proc_compativel ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_procedimento_comp_p bigint) RETURNS varchar AS $body$
DECLARE

cd_procedimento_w	bigint;
qt_registro_w		bigint;
ds_retorno_w		varchar(10)	:= 'N';
qt_regra4_w		bigint	:= 0;
cd_grupo_w		varchar(10)	:= 'X';
qt_comp_w		bigint	:= 0;

c01 CURSOR FOR
	SELECT	cd_proc_secundario
	from	sus_proc_compativel
	where	cd_proc_principal    	= cd_procedimento_p
	and	ie_origem_proc_princ 	= ie_origem_proced_p
	and	ie_tipo_compatibilidade	in (1,4,5);

c02 CURSOR FOR
	SELECT	cd_proc_principal
	from	sus_proc_compativel
	where 	cd_proc_secundario    	= cd_procedimento_p
	and   	ie_origem_proc_sec 	= ie_origem_proced_p
	and	ie_tipo_compatibilidade in (1,4,5);
	

BEGIN

qt_regra4_w	:= coalesce(sus_validar_regra(nr_seq_regra_p => 4,cd_procedimento_p => cd_procedimento_p,ie_origem_proced_p => ie_origem_proced_p,dt_competencia_p => null),0);
cd_grupo_w	:= coalesce(sus_obter_estrut_proc(cd_procedimento_p,ie_origem_proced_p,'C','G'),'X');

if (qt_regra4_w = 0) then
	begin
			
	select	count(1)
	into STRICT	qt_comp_w
	from	sus_proc_compativel
	where	cd_proc_principal    	= cd_procedimento_p
	and	ie_origem_proc_princ 	= ie_origem_proced_p
	and	cd_proc_secundario	= cd_procedimento_comp_p
	and	ie_origem_proc_princ	= 7
	and	ie_tipo_compatibilidade	in (1,4,5)  LIMIT 1;
	
	if (qt_comp_w > 0) then
		ds_retorno_w	:= 'S';
	end if;
	
	/*open c01;
	loop
	fetch c01 into
		cd_procedimento_w;
	exit when c01%notfound;
		begin
		ds_retorno_w		:= 'N';
		if	(cd_procedimento_w	= cd_procedimento_comp_p) then
			ds_retorno_w	:= 'S';
			exit;
		end if;
		end;
	end loop;
	close c01;*/
	
	end;
end if;

if (ds_retorno_w = 'N') then
	begin
	
	if	((qt_regra4_w > 0) or (cd_grupo_w in ('6','4','3','2','5'))) then
		begin
		
		select	count(1)
		into STRICT	qt_comp_w
		from	sus_proc_compativel
		where 	cd_proc_secundario    	= cd_procedimento_p
		and   	ie_origem_proc_sec 	= ie_origem_proced_p
		and	cd_proc_principal	= cd_procedimento_comp_p
		and	ie_origem_proc_princ	= 7
		and	ie_tipo_compatibilidade in (1,4,5)  LIMIT 1;
		
		if (qt_comp_w > 0) then
			ds_retorno_w	:= 'S';
		end if;
		
		/*open c02;
		loop
		fetch c02 into
			cd_procedimento_w;
		exit when c02%notfound;
			begin
			ds_retorno_w		:= 'N';
			if	(cd_procedimento_w	= cd_procedimento_comp_p) then
				ds_retorno_w	:= 'S';
				exit;
			end if;
			end;
		end loop;
		close c02;*/
		
		end;
	end if;

	if (ds_retorno_w = 'N') and
		((sus_obter_tiporeg_proc(cd_procedimento_p,ie_origem_proced_p,'C',2) = 3) and (sus_validar_regra(nr_seq_regra_p => 11,cd_procedimento_p => cd_procedimento_comp_p,ie_origem_proced_p => 7,dt_competencia_p => null) > 0) and
		cd_procedimento_comp_p <> 415020050) or
		((sus_obter_tiporeg_proc(cd_procedimento_comp_p,7,'C',2) = 3) and (sus_validar_regra(nr_seq_regra_p => 11,cd_procedimento_p => cd_procedimento_p,ie_origem_proced_p => ie_origem_proced_p,dt_competencia_p => null) > 0)) then
		ds_retorno_w := 'S';
	end if;
	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
