CREATE OR REPLACE FUNCTION sus_obter_se_proc_gera_apac ( nr_seq_interno_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(15) := 'N';
nr_seq_forma_org_w	bigint;
nr_seq_subgrupo_w	bigint;
nr_seq_grupo_w		bigint;
cd_procedimento_w	bigint := 0;
ie_origem_proced_w	bigint := 7;
qt_reg_ger_apac_w	bigint := 0;


BEGIN

begin
select	cd_procedimento_solic,
	ie_origem_proced
into STRICT	cd_procedimento_w,
	ie_origem_proced_w
from	sus_laudo_paciente
where	nr_seq_interno = nr_seq_interno_p;
exception
when others then
	cd_procedimento_w := 0;
end;

if (cd_procedimento_w <> 0) and (ie_origem_proced_w = 7) then
	begin

	select	nr_seq_forma_org,
		nr_seq_subgrupo,
		nr_seq_grupo
	into STRICT	nr_seq_forma_org_w,
		nr_seq_subgrupo_w,
		nr_seq_grupo_w
	from	sus_estrutura_procedimento_v
	where	cd_procedimento = cd_procedimento_w
	and	ie_origem_proced = ie_origem_proced_w;

	begin
	select	count(*)
	into STRICT	qt_reg_ger_apac_w
	from	sus_regra_proc_gera_apac
	where	ie_situacao = 'A'
	and	coalesce(nr_seq_forma_org,nr_seq_forma_org_w) = nr_seq_forma_org_w
	and	coalesce(nr_seq_subgrupo,nr_seq_subgrupo_w) = nr_seq_subgrupo_w
	and	coalesce(nr_seq_grupo,nr_seq_grupo_w) = nr_seq_grupo_w
	and	coalesce(cd_procedimento,cd_procedimento_w) = cd_procedimento_w
	and	coalesce(ie_origem_proced,ie_origem_proced_w) = ie_origem_proced_w;
	exception
	when others then
		qt_reg_ger_apac_w := 0;
	end;

	if (qt_reg_ger_apac_w > 0) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
