CREATE OR REPLACE FUNCTION sus_obter_se_proc_hemo (nr_sequencia_p procedimento_paciente.nr_sequencia%type) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1)	:= 'N';
qt_reg_hemo_w		bigint	:= 0;


BEGIN

select	count(1)
into STRICT	qt_reg_hemo_w
from	san_exame_realizado
where   nr_seq_propaci  = nr_sequencia_p  LIMIT 1;

if (qt_reg_hemo_w = 0) then
	begin
	select	count(1)
	into STRICT	qt_reg_hemo_w
	from	san_producao
	where   nr_seq_propaci  = nr_sequencia_p  LIMIT 1;

	if (qt_reg_hemo_w = 0) then
		begin
		select	count(1)
		into STRICT	qt_reg_hemo_w
		from	san_reserva_prod
		where   nr_seq_propaci  = nr_sequencia_p  LIMIT 1;

		if (qt_reg_hemo_w > 0) then
			ds_retorno_w := 'S';
		end if;
		end;
	else
		ds_retorno_w := 'S';
	end if;
	end;
else
	ds_retorno_w := 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
