CREATE OR REPLACE FUNCTION sus_obter_se_proc_sh ( cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_forma_org_w		bigint := 0;
nr_seq_grupo_w			bigint := 0;
nr_seq_subgrupo_w		bigint := 0;
qt_registro_w			bigint;
ds_retorno_w			varchar(10) := 'N';

C01 CURSOR FOR
	SELECT	a.ie_calcula_rateio
	from	sus_regra_rateio_sh_crit a
	where	cd_estabelecimento				= cd_estabelecimento_p
	and	coalesce(a.cd_procedimento,cd_procedimento_p) 	= cd_procedimento_p
	and	coalesce(a.ie_origem_proced,ie_origem_proced_p)	= ie_origem_proced_p
	and	coalesce(a.nr_seq_grupo,nr_seq_grupo_w)		= nr_seq_grupo_w
	and	coalesce(a.nr_seq_subgrupo,nr_seq_subgrupo_w)	= nr_seq_subgrupo_w
	and	coalesce(a.nr_seq_forma_org,nr_seq_forma_org_w)	= nr_seq_forma_org_w
	and	coalesce(ie_situacao,'A') = 'A'
	order by 	coalesce(a.cd_procedimento,0),
			coalesce(a.nr_seq_forma_org,0),
			coalesce(a.nr_seq_subgrupo,0),
			coalesce(a.nr_seq_grupo,0);


BEGIN

select	count(1)
into STRICT	qt_registro_w
from	sus_regra_rateio_sh_crit
where	cd_estabelecimento	= cd_estabelecimento_p
and	coalesce(ie_situacao,'A') 	= 'A'  LIMIT 1;

if (qt_registro_w		= 0 ) or (ie_origem_proced_p	not in (4,7)) then
	ds_retorno_w	:= 'S';
else
	begin

	begin
	select	coalesce(d.nr_seq_forma_org,0),
		coalesce(c.nr_seq_subgrupo,0),
		coalesce(b.nr_seq_grupo,0)
	into STRICT	nr_seq_forma_org_w,
		nr_seq_subgrupo_w,
		nr_seq_grupo_w
	from	sus_subgrupo b,
		sus_forma_organizacao c,
		sus_procedimento d
	where	b.nr_sequencia		= c.nr_seq_subgrupo
	and	c.nr_sequencia		= d.nr_seq_forma_org
	and	d.cd_procedimento	= cd_procedimento_p
	and	d.ie_origem_proced	= ie_origem_proced_p;
	exception
	when others then
		nr_seq_forma_org_w	:= 0;
		nr_seq_subgrupo_w	:= 0;
		nr_seq_grupo_w		:= 0;
	end;

	open C01;
	loop
	fetch C01 into
		ds_retorno_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
	end loop;
	close C01;

	end;
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
