CREATE OR REPLACE FUNCTION sus_obter_se_regra_agrup_proc ( cd_estabelecimento_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_forma_org_w		bigint := 0;
nr_seq_grupo_w			bigint := 0;
nr_seq_subgrupo_w		bigint := 0;
qt_registro_w			bigint;


BEGIN

begin
select	nr_seq_forma_org,
	nr_seq_grupo,
	nr_seq_subgrupo
into STRICT	nr_seq_forma_org_w,
	nr_seq_grupo_w,
	nr_seq_subgrupo_w
from	sus_estrutura_procedimento_v a
where	a.cd_procedimento	= cd_procedimento_p
and	a.ie_origem_proced	= ie_origem_proced_p;
exception
	when others then
	null;
end;


select	count(*)
into STRICT	qt_registro_w
from	sus_regra_agrup_proc a
where	a.cd_estabelecimento	 = cd_estabelecimento_p
and	coalesce(a.cd_procedimento,cd_procedimento_p) 		= cd_procedimento_p
and	((coalesce(cd_procedimento::text, '') = '') or (coalesce(a.ie_origem_proced,ie_origem_proced_p)= ie_origem_proced_p))
and	coalesce(a.nr_seq_grupo,nr_seq_grupo_w)			= nr_seq_grupo_w
and	coalesce(a.nr_seq_subgrupo,nr_seq_subgrupo_w)		= nr_seq_subgrupo_w
and	coalesce(a.nr_seq_forma_org,nr_seq_forma_org_w)		= nr_seq_forma_org_w
and	a.ie_situacao 						= 'A';

if (qt_registro_w	> 0) then
	return	'S';
else
	return 'N';
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
