CREATE OR REPLACE FUNCTION sus_obter_sql_interface ( nr_seq_protocolo_p bigint, cd_interface_p bigint) RETURNS varchar AS $body$
DECLARE



ds_sql_interface_w		interface.ds_comando%type := 'X';


BEGIN

begin
select	coalesce(max(b.ds_comando),'X')
into STRICT	ds_sql_interface_w
from	interface b
where	b.cd_interface	= cd_interface_p
and	(b.ds_comando IS NOT NULL AND b.ds_comando::text <> '');
exception
when others then
	ds_sql_interface_w := 'X';
end;

if (ds_sql_interface_w <> 'X') then
	ds_sql_interface_w := replace(lower(ds_sql_interface_w),':nr_seq_protocolo',to_char(nr_seq_protocolo_p));
else
	ds_sql_interface_w := '';
end if;

return ds_sql_interface_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
