CREATE OR REPLACE FUNCTION sus_obter_substituir_cgc ( ie_tipo_servico_sus_p bigint, ie_tipo_ato_sus_p bigint, cd_cgc_prestador_p text, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


qt_regra_w		integer;
cd_cgc_result_w		varchar(14);
ie_tipo_ato_sus_w	smallint;
ie_tipo_servico_sus_w	smallint;

c01 CURSOR FOR
	SELECT	cd_cgc_destino
	from	sus_aih_substituir_cgc
	where	coalesce(ie_tipo_servico_sus, ie_tipo_servico_sus_w) = ie_tipo_servico_sus_w
	and	coalesce(ie_tipo_ato_sus, ie_tipo_ato_sus_w) = ie_tipo_ato_sus_w
	and	coalesce(cd_cgc_origem, cd_cgc_prestador_p) = cd_cgc_prestador_p
	order by
		coalesce(ie_tipo_servico_sus, 0), coalesce(ie_tipo_ato_sus, 0), coalesce(cd_cgc_origem, '');

BEGIN

select	count(*)
into STRICT	qt_regra_w
from	sus_aih_substituir_cgc;

if (qt_regra_w > 0) then
	cd_cgc_result_w		:= cd_cgc_prestador_p;
	ie_tipo_ato_sus_w	:= coalesce(ie_tipo_ato_sus_p,0);
	ie_tipo_servico_sus_w	:= coalesce(ie_tipo_servico_sus_p,0);

	open c01;
	loop
	fetch c01 into cd_cgc_result_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
	end loop;
	close c01;
else
	cd_cgc_result_w	:= cd_cgc_prestador_p;
end if;

return coalesce(cd_cgc_result_w, cd_cgc_prestador_p);

end;
$body$
