SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_tipomedico_aih ( nr_atendimento_p bigint, ie_tipo_medico_p text) RETURNS varchar AS $body$
DECLARE


cd_medico_resp_w	        varchar(10);
cd_diretor_clinico_w	        varchar(10);
cd_medico_autorizador_w	        varchar(10);
cd_medico_retorno_w	        varchar(10)	:= '';
cd_medico_executor_w	        varchar(10);
cd_medico_responsavel_w	        varchar(20);
dt_procedimento_w               procedimento_paciente.dt_procedimento%type;
dt_liberacao_aih_w              sus_laudo_paciente.dt_liberacao%type;
cd_medico_requisitante_w        sus_laudo_paciente.cd_medico_requisitante%type;


BEGIN

begin
select	a.cd_medico_resp,
	b.cd_diretor_clinico,
	b.cd_medico_autorizador,
	b.cd_medico_responsavel
into STRICT	cd_medico_resp_w,
	cd_diretor_clinico_w,
	cd_medico_autorizador_w,
	cd_medico_responsavel_w
from	sus_parametros_aih	b,
	atendimento_paciente	a
where	a.nr_atendimento	= nr_atendimento_p
and	a.cd_estabelecimento	= b.cd_estabelecimento;
exception
when others then
        CALL Wheb_mensagem_pck.exibir_mensagem_abort(250857);--'Problema na leitura do medico!'
end;

if (ie_tipo_medico_p	= 'A') then
	cd_medico_retorno_w	:= cd_medico_resp_w;
elsif (ie_tipo_medico_p	= 'D') then
	cd_medico_retorno_w	:= cd_diretor_clinico_w;
elsif (ie_tipo_medico_p	= 'T') then
	cd_medico_retorno_w	:= cd_medico_autorizador_w;
elsif (ie_tipo_medico_p	= 'R') then
	cd_medico_retorno_w	:= cd_medico_responsavel_w;
elsif (ie_tipo_medico_p	= 'P') then
	begin
	
	begin
	select 	max(x.dt_procedimento)
	into STRICT	dt_procedimento_w
	from 	procedimento_paciente x,
		conta_paciente y
	where	y.nr_atendimento	= nr_atendimento_p
	and	x.nr_interno_conta	= y.nr_interno_conta
	and	coalesce(x.cd_motivo_exc_conta::text, '') = ''
	and	sus_obter_tipoReg_proc(x.cd_procedimento,x.ie_origem_proced,'C',13) = 3;	
	exception
	when others then
		dt_procedimento_w := null;
	end;
						
	begin
	select	p.cd_medico_executor
	into STRICT	cd_medico_executor_w
	from	procedimento_paciente p,
		conta_paciente c
	where	c.nr_atendimento	= nr_atendimento_p
	and	p.nr_interno_conta	= c.nr_interno_conta
	and	coalesce(p.cd_motivo_exc_conta::text, '') = ''
	and	p.dt_procedimento  	= dt_procedimento_w
	and	sus_obter_tipoReg_proc(p.cd_procedimento,p.ie_origem_proced,'C',13) = 3;
	exception
	when others then		
                CALL Wheb_mensagem_pck.exibir_mensagem_abort(250857);--'Problema na leitura do medico!'
        end;		

	cd_medico_retorno_w	:= cd_medico_executor_w;
	end;
elsif (ie_tipo_medico_p	= 'L') then
        begin
        
        begin
        select 	max(s.dt_liberacao)
        into STRICT    dt_liberacao_aih_w
        from 	sus_laudo_paciente s
        where	s.nr_atendimento	= nr_atendimento_p
        and     s.ie_classificacao      = 1
        and     s.ie_tipo_laudo_sus     = 0;
        exception
        when others then
                dt_liberacao_aih_w := null;
        end;

        begin    
        select	s.cd_medico_requisitante
        into STRICT	cd_medico_requisitante_w
        from	sus_laudo_paciente s
        where	s.nr_atendimento        = nr_atendimento_p
        and     s.ie_classificacao      = 1
        and     s.ie_tipo_laudo_sus     = 0
        and     s.dt_liberacao = dt_liberacao_aih_w;
        exception
        when others then
                cd_medico_requisitante_w := null;
        end;

        cd_medico_retorno_w	:= cd_medico_requisitante_w;

        end;
end if;

return	cd_medico_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION sus_obter_tipomedico_aih ( nr_atendimento_p bigint, ie_tipo_medico_p text) FROM PUBLIC;

