SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_tipo_doc ( cd_pessoa_fisica_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE



/* ie_opcao_p

	D - Documento
	T - Tipo de Documento
*/
nr_pis_pasep_w			varchar(11);
nr_identidade_w			varchar(11);
nr_cert_nasc_w			varchar(11);
nr_cpf_w			varchar(11);
nr_doc_paciente_w		varchar(11);
ds_doc_paciente_w		varchar(40);
ds_retorno_w			varchar(50);



BEGIN
select	c.nr_pis_pasep,
	substr(c.nr_identidade,1,11),
	substr(c.nr_cert_nasc,1,11) nr_cert_nasc,
	c.nr_cpf
into STRICT	nr_pis_pasep_w,
	nr_identidade_w,
	nr_cert_nasc_w,
	nr_cpf_w
from	pessoa_fisica	c
where	c.cd_pessoa_fisica	= cd_pessoa_fisica_p;

/* Retorna o PIS-PASEP/Identidade/RG/CPF*/

if (coalesce(nr_doc_paciente_w::text, '') = '') and (nr_pis_pasep_w IS NOT NULL AND nr_pis_pasep_w::text <> '') then
        nr_doc_paciente_w	:= nr_pis_pasep_w;
        ds_doc_paciente_w   	:= 'PIS-PASEP';
elsif (coalesce(nr_doc_paciente_w::text, '') = '') and (nr_identidade_w IS NOT NULL AND nr_identidade_w::text <> '') then
        nr_doc_paciente_w  	:= nr_identidade_w;
        ds_doc_paciente_w   	:= 'Identidade';
elsif (coalesce(nr_doc_paciente_w::text, '') = '') and (nr_cert_nasc_w IS NOT NULL AND nr_cert_nasc_w::text <> '') then
        nr_doc_paciente_w  	:= nr_cert_nasc_w;
        ds_doc_paciente_w   	:= 'RG';
elsif (coalesce(nr_doc_paciente_w::text, '') = '') and (nr_cpf_w IS NOT NULL AND nr_cpf_w::text <> '') then
        nr_doc_paciente_w  	:= nr_cpf_w;
        ds_doc_paciente_w   	:= 'CPF';
else
        nr_doc_paciente_w  	:= ' ';
        ds_doc_paciente_w   	:= '';
end if;

if (ie_opcao_p	= 'T') then
	ds_retorno_w	:= ds_doc_paciente_w;
elsif (ie_opcao_p	= 'D') then
	ds_retorno_w	:= nr_doc_paciente_w;
end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION sus_obter_tipo_doc ( cd_pessoa_fisica_p text, ie_opcao_p text) FROM PUBLIC;

