SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_total_uti_conv ( dt_referencia_p text, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE

							 
qt_retorno_w		double precision;
qt_tipo_1_conv_w	double precision;
qt_tipo_2_3_conv_w	double precision;
qt_pediatrica_w		double precision;
qt_neonatal_w		double precision;
qt_quimados_w		double precision;
					

BEGIN 
 
select	count(distinct a.nr_atendimento) 
into STRICT	qt_tipo_1_conv_w 
from	procedimento_paciente x, 
	atend_categoria_convenio c, 
	parametro_faturamento f, 
	convenio b, 
	atendimento_paciente a 
where	trunc(a.dt_entrada,'yyyy') = to_date('01/01'||dt_referencia_p,'dd/mm/yyyy') 
and	a.nr_atendimento	= c.nr_atendimento 
and	c.cd_convenio		= b.cd_convenio 
and	a.ie_tipo_atendimento	= 1 
and	a.cd_estabelecimento	= cd_estabelecimento_p 
and 	b.ie_tipo_convenio	<> 3 
and	x.nr_atendimento	= a.nr_atendimento 
and	obter_cod_proc_interno_conv(x.nr_seq_proc_interno,f.cd_convenio_sus,null,clock_timestamp(),a.cd_estabelecimento) = 802010105 
and	f.cd_estabelecimento	= a.cd_estabelecimento;
 
select	count(distinct a.nr_atendimento) 
into STRICT	qt_tipo_2_3_conv_w 
from	procedimento_paciente x, 
	atend_categoria_convenio c, 
	parametro_faturamento f, 
	convenio b, 
	atendimento_paciente a 
where	trunc(a.dt_entrada,'yyyy') = to_date('01/01'||dt_referencia_p,'dd/mm/yyyy') 
and	a.nr_atendimento	= c.nr_atendimento 
and	c.cd_convenio		= b.cd_convenio 
and	a.ie_tipo_atendimento	= 1 
and	a.cd_estabelecimento	= cd_estabelecimento_p 
and 	b.ie_tipo_convenio	<> 3 
and	x.nr_atendimento	= a.nr_atendimento 
and	obter_cod_proc_interno_conv(x.nr_seq_proc_interno,f.cd_convenio_sus,null,clock_timestamp(),a.cd_estabelecimento) in (802010083,802010091) 
and	f.cd_estabelecimento	= a.cd_estabelecimento;
 
select	count(distinct a.nr_atendimento) 
into STRICT	qt_pediatrica_w 
from	procedimento_paciente x, 
	atend_categoria_convenio c, 
	parametro_faturamento f, 
	convenio b, 
	atendimento_paciente a 
where	trunc(a.dt_entrada,'yyyy') = to_date('01/01'||dt_referencia_p,'dd/mm/yyyy') 
and	a.nr_atendimento	= c.nr_atendimento 
and	c.cd_convenio		= b.cd_convenio 
and	a.ie_tipo_atendimento	= 1 
and	a.cd_estabelecimento	= cd_estabelecimento_p 
and 	b.ie_tipo_convenio	<> 3 
and	x.nr_atendimento	= a.nr_atendimento 
and	obter_cod_proc_interno_conv(x.nr_seq_proc_interno,f.cd_convenio_sus,null,clock_timestamp(),a.cd_estabelecimento) in (802010148,802010156,802010075) 
and	f.cd_estabelecimento	= a.cd_estabelecimento;
 
select	count(distinct a.nr_atendimento) 
into STRICT	qt_neonatal_w 
from	procedimento_paciente x, 
	atend_categoria_convenio c, 
	parametro_faturamento f, 
	convenio b, 
	atendimento_paciente a 
where	trunc(a.dt_entrada,'yyyy') = to_date('01/01'||dt_referencia_p,'dd/mm/yyyy') 
and	a.nr_atendimento	= c.nr_atendimento 
and	c.cd_convenio		= b.cd_convenio 
and	a.ie_tipo_atendimento	= 1 
and	a.cd_estabelecimento	= cd_estabelecimento_p 
and 	b.ie_tipo_convenio	<> 3 
and	x.nr_atendimento	= a.nr_atendimento 
and	obter_cod_proc_interno_conv(x.nr_seq_proc_interno,f.cd_convenio_sus,null,clock_timestamp(),a.cd_estabelecimento) in (802010164,802010130,802010121) 
and	f.cd_estabelecimento	= a.cd_estabelecimento;
 
select	count(distinct a.nr_atendimento) 
into STRICT	qt_quimados_w 
from	procedimento_paciente x, 
	atend_categoria_convenio c, 
	parametro_faturamento f, 
	convenio b, 
	atendimento_paciente a 
where	trunc(a.dt_entrada,'yyyy') = to_date('01/01'||dt_referencia_p,'dd/mm/yyyy') 
and	a.nr_atendimento	= c.nr_atendimento 
and	c.cd_convenio		= b.cd_convenio 
and	a.ie_tipo_atendimento	= 1 
and	a.cd_estabelecimento	= cd_estabelecimento_p 
and 	b.ie_tipo_convenio	<> 3 
and	x.nr_atendimento	= a.nr_atendimento 
and	obter_cod_proc_interno_conv(x.nr_seq_proc_interno,f.cd_convenio_sus,null,clock_timestamp(),a.cd_estabelecimento) = 802010113 
and	f.cd_estabelecimento	= a.cd_estabelecimento;
 
qt_retorno_w := coalesce(qt_tipo_1_conv_w,0) + (coalesce(qt_tipo_2_3_conv_w,0) * 1.1) + (coalesce(qt_pediatrica_w,0) * 1.15) + (coalesce(qt_neonatal_w,0) * 1.25) + (coalesce(qt_quimados_w,0) * 1.2);
 
return coalesce(qt_retorno_w,0);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
