SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_tp_laudo_siscolo ( nr_seq_laudo_p bigint, it_tipo_retorno_p bigint) RETURNS varchar AS $body$
DECLARE

qt_laudo_cito_w		bigint := 0;
qt_laudo_histo_w	bigint := 0;
ds_retorno_w		varchar(100);
/*Tipo retorno:
	1 - número:
		1 - citopatológico
		2 - histopatológico
	2 - descrição*/
BEGIN

select	count(*)
into STRICT	qt_laudo_cito_w
from	siscolo_cito_anamnese
where	nr_seq_siscolo = nr_seq_laudo_p;

if (qt_laudo_cito_w = 0) then
	begin
	select	count(*)
	into STRICT	qt_laudo_cito_w
	from	siscolo_cito_atipias
	where	nr_seq_siscolo = nr_seq_laudo_p;
	if (qt_laudo_cito_w = 0) then
		begin
		select	count(*)
		into STRICT	qt_laudo_cito_w
		from	siscolo_cito_avaliacao
		where	nr_seq_siscolo = nr_seq_laudo_p;
		if (qt_laudo_cito_w = 0) then
			begin
			select	count(*)
			into STRICT	qt_laudo_cito_w
			from	siscolo_cito_diagnostico
			where	nr_seq_siscolo = nr_seq_laudo_p;
			end;
		end if;
		end;
	end if;
	end;
end if;

select	count(*)
into STRICT	qt_laudo_histo_w
from	siscolo_histo_continuacao
where	nr_seq_siscolo = nr_seq_laudo_p;

if (qt_laudo_histo_w = 0) then
	begin
	select	count(*)
	into STRICT	qt_laudo_histo_w
	from	siscolo_histo_result
	where	nr_seq_siscolo = nr_seq_laudo_p;
	if (qt_laudo_histo_w = 0) then
		begin
		select	count(*)
		into STRICT	qt_laudo_histo_w
		from	siscolo_histo_result_cito
		where	nr_seq_siscolo = nr_seq_laudo_p;
		end;
	end if;
	end;
end if;

if (qt_laudo_cito_w > 0) then
	begin
	if (it_tipo_retorno_p = 1) then
		ds_retorno_w := '1';
	else
		--ds_retorno_w := 'Citopatológico';
		ds_retorno_w := wheb_mensagem_pck.get_texto(313993);
	end if;
	end;
elsif (qt_laudo_histo_w > 0) then
	begin
	if (it_tipo_retorno_p = 1) then
		ds_retorno_w := '2';
	else
		--ds_retorno_w := 'Histopatológico';
		ds_retorno_w := wheb_mensagem_pck.get_texto(313994);
	end if;
	end;
end if;

return	coalesce(ds_retorno_w,'');

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION sus_obter_tp_laudo_siscolo ( nr_seq_laudo_p bigint, it_tipo_retorno_p bigint) FROM PUBLIC;

