CREATE OR REPLACE FUNCTION sus_obter_ult_atend_aghos ( cd_pessoa_fisica_p bigint) RETURNS bigint AS $body$
DECLARE

 
nr_internado_w			bigint;

 

BEGIN 
if (coalesce(cd_pessoa_fisica_p,0) > 0) then 
 
	select	max(b.nr_internacao) 
	into STRICT	nr_internado_w 
	from	atendimento_paciente a, 
			solicitacao_tasy_aghos B 
	where  a.cd_pessoa_fisica = cd_pessoa_fisica_p 
	AND		a.nr_atendimento = b.nr_atendimento 
	and		a.ie_tipo_atendimento = 1 
	and (clock_timestamp() - a.dt_alta) <= 3;
	 
end if;
 
 
return	nr_internado_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
