SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_valor_item_proc ( nr_sequencia_p bigint, ie_tipo_p bigint) RETURNS bigint AS $body$
DECLARE


/* IE_TIPO_P
	1 - Matmed
	2 - Medico
	3 - SADT
	4 - Vl Ato medico
	5 - Qt ato medico
	6 - Vl Ato anestesista
	7 - Vl - Total
*/
vl_retorno_w		double precision;
vl_ato_medico_w		double precision;
vl_matmed_w		double precision;
vl_medico_w		double precision;
vl_sadt_w		double precision;
qt_ato_medico_w		double precision;
qt_ato_anestesista_w	double precision;
vl_ato_anestesista_w	double precision;


BEGIN

select	coalesce(b.vl_matmed,0),
	coalesce(b.vl_medico,0),
	coalesce(b.vl_sadt,0),
	coalesce(b.vl_ato_medico,0),
	coalesce(b.qt_ato_medico,0),
	coalesce(b.vl_ato_anestesista,0)
into STRICT	vl_matmed_w,
	vl_medico_w,
	vl_sadt_w,
	vl_ato_medico_w,
	qt_ato_medico_w,
	vl_ato_anestesista_w
from 	sus_valor_proc_paciente	b
where 	b.nr_sequencia		= nr_sequencia_p;

if (ie_tipo_p	= 1) then
	vl_retorno_w	:= vl_matmed_w;
elsif (ie_tipo_p	= 2) then
	vl_retorno_w	:= vl_medico_w;
elsif (ie_tipo_p	= 3) then
	vl_retorno_w	:= vl_sadt_w;
elsif (ie_tipo_p	= 4) then
	vl_retorno_w	:= vl_ato_medico_w;
elsif (ie_tipo_p	= 5) then
	vl_retorno_w	:= qt_ato_medico_w;
elsif (ie_tipo_p	= 6) then
	vl_retorno_w	:= vl_ato_anestesista_w;
elsif (ie_tipo_p	= 7) then
	vl_retorno_w	:= vl_ato_anestesista_w + vl_matmed_w + vl_sadt_w + vl_medico_w;
end if;

return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
