CREATE OR REPLACE FUNCTION sus_obter_valor_porte_anest ( cd_edicao_amb_p bigint, nr_porte_anestesico_p bigint, dt_inicio_vigencia_p timestamp, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


vl_retorno_w		varchar(255);
cd_edicao_amb_w		integer;
nr_porte_anestesico_w	smallint;
dt_inicio_vigencia_w	timestamp;

/*ie_opcao:
V - valor
P - Porte e valor
*/
BEGIN

begin
select	campo_mascara_virgula(vl_porte_anestesico)
into STRICT	vl_retorno_w
from	porte_anestesico
where	cd_edicao_amb 		= cd_edicao_amb_p
and	nr_porte_anestesico 	= nr_porte_anestesico_p
and	dt_inicio_vigencia		= dt_inicio_vigencia_p;
exception
when others then
	vl_retorno_w := '';
end;

if (ie_opcao_p = 'P') then
	vl_retorno_w := nr_porte_anestesico_p||' - '||vl_retorno_w;
end if;

return vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
