SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_obter_valor_proc_pac (nr_interno_conta_p bigint, ie_tipo_valor_p bigint) RETURNS bigint AS $body$
DECLARE


/* ie_tipo_valor_p

	0 - SADT

*/
vl_retorno_w	double precision := 0;
vl_sadt_w	double precision := 0;



BEGIN

select	sum(b.vl_sadt)
into STRICT	vl_sadt_w
from	procedimento_paciente a,
	sus_valor_proc_paciente b
where	a.nr_sequencia		= b.nr_sequencia
and	a.nr_interno_conta	= nr_interno_conta_p
and	coalesce(a.cd_motivo_exc_conta::text, '') = '';

if (ie_tipo_valor_p	= 0) then
	vl_retorno_w		:= vl_sadt_w;
end if;


return	vl_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
