CREATE OR REPLACE FUNCTION sus_obter_valor_proc_unif ( dt_procedimento_p timestamp, cd_procedimento_p bigint, ie_origem_proced_p bigint, cd_estabelecimento_p bigint, ie_tipo_atendimento_p bigint) RETURNS bigint AS $body$
DECLARE

vl_sa_w 		double precision;
vl_sh_w 		double precision;
vl_sp_w 		double precision;
vl_sadt_w 		double precision;
vl_total_hospitalar_w 	double precision;
vl_total_amb_w 		double precision;
qt_pontos_ato_w 	double precision;
vl_honorario_medico_w 	double precision;
vl_matmed_w 		double precision;
vl_anestesista_w 	double precision;
dt_competencia_w	timestamp;
vl_total_sus_w		double precision;


BEGIN

SELECT * FROM Sus_Define_Preco_Proced(dt_procedimento_p, cd_procedimento_p, ie_origem_proced_p, cd_estabelecimento_p, ie_tipo_atendimento_p, vl_sa_w, vl_sh_w, vl_sp_w, vl_sadt_w, vl_total_hospitalar_w, vl_total_amb_w, qt_pontos_ato_w, vl_honorario_medico_w, vl_matmed_w, vl_anestesista_w, dt_competencia_w) INTO STRICT vl_sa_w, vl_sh_w, vl_sp_w, vl_sadt_w, vl_total_hospitalar_w, vl_total_amb_w, qt_pontos_ato_w, vl_honorario_medico_w, vl_matmed_w, vl_anestesista_w, dt_competencia_w;

if (ie_tipo_atendimento_p = 1) then
	vl_total_sus_w	:= coalesce(vl_total_hospitalar_w,0);
else
	vl_total_sus_w	:= coalesce(vl_total_amb_w,0);
end if;

return vl_total_sus_w;

end;
