CREATE OR REPLACE FUNCTION sus_obter_valor_protocolo (nr_seq_protocolo_p bigint) RETURNS bigint AS $body$
DECLARE

 
vl_total_w		procedimento_paciente.vl_procedimento%type := 0;
nr_interno_conta_w	conta_paciente.nr_interno_conta%type;
ie_arred_sp_sisaih_w	sus_parametros_aih.ie_arred_sp_sisaih%type;

C01 CURSOR FOR 
	SELECT	coalesce(Sus_Obter_Valor_Unif(nr_interno_conta,0),0) vl_conta_sus 
	from	conta_paciente 
	where	nr_seq_protocolo = nr_seq_protocolo_p;
	
type 		fetch_array is table of c01%rowtype;
s_array 	fetch_array;
i		integer := 1;
type vetor is table of fetch_array index by integer;
vetor_c01_w			vetor;

BEGIN 
 
begin 
select	coalesce(ie_arred_sp_sisaih,'N') 
into STRICT	ie_arred_sp_sisaih_w 
from	protocolo_convenio a, 
	sus_parametros_aih b 
where	a.nr_seq_protocolo	= nr_seq_protocolo_p 
and	b.cd_estabelecimento	= a.cd_estabelecimento  LIMIT 1;
exception 
when others then 
	ie_arred_sp_sisaih_w := 'N';
end;
 
open c01;
loop 
fetch c01 bulk collect into s_array limit 100000;
	vetor_c01_w(i) := s_array;
	i := i + 1;
EXIT WHEN NOT FOUND; /* apply on c01 */
end loop;
close c01;
 
for i in 1..vetor_c01_w.count loop 
	begin 
	s_array := vetor_c01_w(i);
	for z in 1..s_array.count loop 
		begin 
		 
		vl_total_w	:= vl_total_w + coalesce(s_array[z].vl_conta_sus,0);
 
		end;
	end loop;
	 
	end;
end loop;
 
return coalesce(vl_total_w,0);
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
