CREATE OR REPLACE FUNCTION sus_obter_valor ( nr_interno_conta_p bigint, ie_valor_p bigint) RETURNS bigint AS $body$
DECLARE

vl_retorno_w	double precision;


BEGIN

select	sum(coalesce(vl_sadt,0)) + sum(coalesce(vl_ato_anestesista,0)) + sum(coalesce(b.vl_medico,0)) + sum(coalesce(vl_matmed,0))
into STRICT	vl_retorno_w
from	sus_valor_proc_paciente b,
	procedimento_paciente a
where	a.nr_sequencia		= b.nr_sequencia
  and	coalesce(a.cd_motivo_exc_conta::text, '') = ''
  and	a.nr_interno_conta	= nr_interno_conta_p
/*  and	a.cd_procedimento	= cd_procedimento_p
  and	a.ie_origem_proced	= ie_origem_proced_p*/
;

return coalesce(vl_retorno_w,0);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
