SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION sus_permite_nova_apac (nr_atendimento_p bigint, cd_doenca_cid_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(1);
dt_fim_validade_w		timestamp;
cd_cid_principal_w		varchar(4);
nr_sequencia_w		bigint;


BEGIN


begin
select	max(a.nr_sequencia)
into STRICT	nr_sequencia_w
from	sus_apac_unif a
where	a.nr_atendimento = nr_atendimento_p
and	a.cd_motivo_cobranca <> 26
and	a.dt_fim_validade = (	SELECT	max(x.dt_fim_validade)
				from	sus_apac_unif x
				where	x.nr_atendimento = a.nr_atendimento
				and	x.cd_motivo_cobranca <> 26);
exception
when others then
	ds_retorno_w := 'S';
end;

begin
select	max(dt_fim_validade),
	max(cd_cid_principal)
into STRICT	dt_fim_validade_w,
	cd_cid_principal_w
from	sus_apac_unif
where	nr_atendimento	= nr_atendimento_p
and	nr_sequencia = nr_sequencia_w;
exception
	when others then
	ds_retorno_w := 'S';
end;

if (clock_timestamp() < dt_fim_validade_w) then
	if (coalesce(cd_doenca_cid_p,cd_cid_principal_w) <> cd_cid_principal_w) then
		ds_retorno_w	:= 'S';
	else
		ds_retorno_w	:= 'N';
	end if;
else
	ds_retorno_w	:= 'S';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
