CREATE OR REPLACE FUNCTION sus_trata_espec_pediatria ( cd_procedimento_real_p bigint, cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(5);


BEGIN

select  min(b.cd_espec_leito)
into STRICT	ds_retorno_w
from    sus_espec_leito b,
        sus_procedimento_leito a
where   a.nr_seq_espec_leito = b.nr_sequencia
and     a.cd_procedimento = cd_procedimento_real_p
and     a.ie_origem_proced = 7
and     Obter_Idade_PF(cd_pessoa_fisica_p,clock_timestamp(),'A') < 18;

select  min(b.cd_espec_leito)
into STRICT	ds_retorno_w
from    sus_espec_leito b,
        sus_procedimento_leito a
where   a.nr_seq_espec_leito = b.nr_sequencia
and     a.cd_procedimento = cd_procedimento_real_p
and     a.ie_origem_proced = 7
and     b.cd_espec_leito <> 7
and     Obter_Idade_PF(cd_pessoa_fisica_p,clock_timestamp(),'A') > 18;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
