CREATE OR REPLACE FUNCTION sus_validar_regra ( nr_seq_regra_p bigint, cd_procedimento_p bigint, ie_origem_proced_p bigint, dt_competencia_p timestamp default null) RETURNS bigint AS $body$
DECLARE


ie_regra_w	integer	:= 0;
dt_competencia_w        sus_regra_proc.dt_comp_inicial%type;
dt_competencia_sus_w	parametro_faturamento.dt_competencia_sus%type;
cd_estabelecimento_w	parametro_faturamento.cd_estabelecimento%type;


BEGIN

cd_estabelecimento_w	:= wheb_usuario_pck.get_cd_estabelecimento;
dt_competencia_w	:= establishment_timezone_utils.startOfMonth(dt_competencia_p);

begin
select	coalesce(dt_competencia_sus,dt_competencia_w)
into STRICT	dt_competencia_sus_w
from	parametro_faturamento
where	cd_estabelecimento = cd_estabelecimento_w;
exception
when	others then
	dt_competencia_sus_w := dt_competencia_w;
end;

if (dt_competencia_sus_w < dt_competencia_w) or (coalesce(dt_competencia_w::text, '') = '') then
	begin
	dt_competencia_w := establishment_timezone_utils.startOfMonth(dt_competencia_sus_w);
	end;
end if;

select	count(1)
into STRICT	ie_regra_w
from	sus_regra_proc
where	nr_seq_regra	= nr_seq_regra_p
and	cd_procedimento	= cd_procedimento_p
and 	ie_origem_proced = ie_origem_proced_p
and  	dt_comp_inicial = dt_competencia_w  LIMIT 1;

if (ie_regra_w 	= 0) and (nr_seq_regra_p 	= 11) and (cd_procedimento_p = 415020050) then
	ie_regra_w := 1;
elsif (ie_regra_w = 0) then
        begin

        select	count(1)
        into STRICT	ie_regra_w
        from	sus_regra_proc
        where	nr_seq_regra	= nr_seq_regra_p
        and	cd_procedimento	= cd_procedimento_p
        and 	ie_origem_proced = ie_origem_proced_p
        and  	(dt_comp_inicial IS NOT NULL AND dt_comp_inicial::text <> '');

        if (ie_regra_w = 0) then
        
                select	count(1)
                into STRICT	ie_regra_w
                from	sus_regra_proc
                where	nr_seq_regra	= nr_seq_regra_p
                and	cd_procedimento	= cd_procedimento_p
                and 	ie_origem_proced = ie_origem_proced_p;

        end if;

        end;
end if;

return	ie_regra_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
