CREATE OR REPLACE FUNCTION sus_verifica_proc_apac ( nr_sequencia_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_proc_w		integer;
nr_atendimento_w	bigint;


BEGIN 
 
select	nr_atendimento 
into STRICT	nr_atendimento_w 
from	sus_apac_movto 
where	nr_sequencia	= nr_sequencia_p;
 
select	count(*) 
into STRICT	qt_proc_w 
from	conta_paciente		c, 
	sus_apac_proc		b, 
	procedimento_paciente	a 
where	a.nr_sequencia		= b.nr_seq_propaci 
and	a.nr_interno_conta	= c.nr_interno_conta 
and	a.nr_atendimento	= nr_atendimento_w 
and	b.nr_seq_apac		= nr_sequencia_p 
and	c.ie_status_acerto	= 2 
and	a.ie_origem_proced	= 3 
and	coalesce(a.cd_motivo_exc_conta::text, '') = '';
 
return	qt_proc_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
