CREATE OR REPLACE FUNCTION sus_verifica_retorno_aih ( nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE

 
nr_atend_original_w	bigint;
qt_aih_retorno_w	integer;
ds_retorno_w		varchar(1)	:= 'N';


BEGIN 
 
select	coalesce(max(nr_atend_original),0) 
into STRICT	nr_atend_original_w 
from	atendimento_paciente 
where	nr_atendimento		= nr_atendimento_p 
and	ie_tipo_atendimento	= 1 
and	ie_tipo_convenio	= 3;
 
select	count(*) 
into STRICT	qt_aih_retorno_w 
from	sus_aih 
where	nr_atendimento	= nr_atend_original_w;
 
if (qt_aih_retorno_w	> 0) then 
	ds_retorno_w	:= 'S';
end if;
 
return	ds_retorno_w;
 
END;
$body$
