CREATE OR REPLACE FUNCTION tab_char ( ds_tab_p text, qt_tab_p bigint) RETURNS varchar AS $body$
DECLARE


ds_tab_w	varchar(4000);
qt_tab_w	bigint := 0;
next_tab_w	bigint := 0;


BEGIN
if (ds_tab_p IS NOT NULL AND ds_tab_p::text <> '') and (qt_tab_p IS NOT NULL AND qt_tab_p::text <> '') then
	begin
	while(qt_tab_w < qt_tab_p) and (next_tab_w < 4000) loop
		begin
		ds_tab_w	:= ds_tab_w || ds_tab_p;
		qt_tab_w	:= qt_tab_w + 1;
		next_tab_w 	:= length(ds_tab_w || ds_tab_p);
		end;
	end loop;
	end;
end if;
return ds_tab_w;
end;
$body$
