CREATE OR REPLACE FUNCTION tasymed_obter_atend_agenda ( dt_agenda_p timestamp, cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE

nr_atendimento_w	bigint;


BEGIN

select	coalesce(max(a.nr_atendimento),0)
into STRICT	nr_atendimento_w
from    med_atendimento a
where   trunc(a.dt_entrada) = dt_agenda_p
and	exists (	SELECT	1
		from    med_cliente b
		where   b.cd_pessoa_fisica	= cd_pessoa_fisica_p
		and 	b.nr_sequencia    	= a.nr_seq_cliente);

return	nr_atendimento_w;

end;
