CREATE OR REPLACE FUNCTION tasy_pda_obter_cor ( nr_seq_cor_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


resultado_w		varchar(15)	:= '';

-- F - cor de fundo
--T - cor de fonte
--S - cor de seleção
BEGIN
resultado_w	:=	OBTER_TASY_OBTER_COR(nr_seq_cor_p, cd_perfil_p, cd_estabelecimento_p, ie_opcao_p);

select	CASE WHEN substr(resultado_w,1,1)='$' THEN '#'||substr(resultado_w,4,255)  ELSE CASE WHEN substr(resultado_w,1,2)='cl' THEN lower(substr(resultado_w,3,255)) END  END
into STRICT	resultado_w
;

return resultado_w;

END;
