SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tax_check_certificate_valid ( sequence_certificate_p tax_certificate.nr_sequencia%type ) RETURNS bigint AS $body$
DECLARE


dt_validity_w 	tax_certificate.dt_validity%type;
ie_valid_w 		smallint := 2;

/*
Types of returns:
0 - Unavailable
1 - Valid
2 - Invalid
*/
BEGIN

	select 	max(dt_validity)
	into STRICT 	dt_validity_w
	from	tax_certificate
	where	nr_sequencia = sequence_certificate_p;

	if (coalesce(dt_validity_w::text, '') = '') then
		ie_valid_w := 0;
	elsif (trunc(dt_validity_w) >= trunc(clock_timestamp())) then
		ie_valid_w := 1;
	end if;
	
	return ie_valid_w;
	
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
