CREATE OR REPLACE FUNCTION tax_invoice_without_pay ( invoice_sequence_p bigint ) RETURNS varchar AS $body$
DECLARE


devolution_w  varchar(1);
transfer_w    varchar(1);


BEGIN
  select	coalesce(max(o.ie_devolucao), 'N'),
          coalesce(max(o.ie_transferencia_estab), 'N')
  into STRICT	  devolution_w,
          transfer_w
  from	  operacao_nota o,
          nota_fiscal a
  where	  a.nr_sequencia = invoice_sequence_p
  and	    a.cd_operacao_nf = o.cd_operacao_nf;

  if (devolution_w = 'S' or transfer_w = 'S') then
    return 'S';
  end if;

  return 'N';
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
