SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tax_limit_content (content_p text, limiter_p text) RETURNS varchar AS $body$
DECLARE

index_w     bigint := 0;
response_w  varchar(255);

BEGIN
  index_w := position(limiter_p in content_p);

  if index_w > 0 then
    response_w := substr(content_p, 0, index_w-1);
  else
    response_w := content_p;
  end if;

  return response_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
