CREATE OR REPLACE FUNCTION tiss_eliminar_caractere (ds_conteudo_p text) RETURNS varchar AS $body$
BEGIN

-- Edgar 07/01/2009, OS 122608, não pode retirar o pipe "|"
return	ltrim(rtrim(replace(
	replace(
	replace(
	replace(
	replace(
	replace(
	replace(
	ds_conteudo_p,	'&', 'e'),
			'<',' '),
			'º', ' '),
			'°', ' '),
			'ª', ' '),
			'"',' '),
			'>', ' ')));

end;
$body$
