SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_cbos_medico (cd_medico_p text, cd_especialidade_p bigint, ie_versao_p text, cd_convenio_p bigint) RETURNS varchar AS $body$
DECLARE


nr_seq_cbo_saude_w	bigint;
cd_cbo_w		varchar(20);
ds_versao_w		varchar(255);

c01 CURSOR FOR  --cursor lido da função de cadastro de médico, pasta especialidade / cbo saúde
	SELECT	b.nr_seq_cbo_saude
	from	tiss_cbo_saude b
	where	b.cd_pessoa_fisica	= cd_medico_p
	and	b.cd_especialidade	= cd_especialidade_p
	and (b.ie_versao = ie_versao_p or coalesce(ie_versao_p::text, '') = '')
	and	coalesce(b.cd_convenio,coalesce(cd_convenio_p,0)) = coalesce(cd_convenio_p,0)
	order by coalesce(b.cd_convenio,0);
	
	
c02 CURSOR FOR  --cursor lido da função cadastro completo de pessoas - pasta cadastro / tiss cbo saúde 
	SELECT	b.nr_seq_cbo_saude
	from	tiss_cbo_saude b
	where	b.cd_pessoa_fisica	= cd_medico_p
	and (b.ie_versao = ie_versao_p or coalesce(ie_versao_p::text, '') = '')
	and	coalesce(b.cd_convenio,coalesce(cd_convenio_p,0)) = coalesce(cd_convenio_p,0)
	order by coalesce(b.cd_convenio,0);

c03 CURSOR FOR  --cursor lido do shift f11 / cadastros gerais / especialidade médica /  tiss cbo saúde
	SELECT	b.nr_seq_cbo_saude
	from	tiss_cbo_saude b
	where	b.cd_especialidade	= cd_especialidade_p
	and	coalesce(b.cd_pessoa_fisica::text, '') = ''
	and (b.ie_versao 		= ie_versao_p or coalesce(ie_versao_p::text, '') = '')
	and	coalesce(b.cd_convenio,coalesce(cd_convenio_p,0)) = coalesce(cd_convenio_p,0)
	order by coalesce(b.cd_convenio,0);


BEGIN

if (coalesce(ie_versao_p,'0') = '0') then --Sem versão informada
	select	coalesce(max(b.nr_seq_cbo_saude),0)
	into STRICT	nr_seq_cbo_saude_w
	from	medico_especialidade b
	where	b.cd_pessoa_fisica	= cd_medico_p
	and	b.cd_especialidade	= cd_especialidade_p;

	if (nr_seq_cbo_saude_w = 0) then

		select	coalesce(max(b.nr_seq_cbo_saude),0)
		into STRICT	nr_seq_cbo_saude_w
		from	pessoa_fisica b
		where	b.cd_pessoa_fisica	= cd_medico_p;

		if (nr_seq_cbo_saude_w = 0) then
			select	coalesce(max(b.nr_seq_cbo_saude),0)
			into STRICT	nr_seq_cbo_saude_w
			from	especialidade_medica b
			where	cd_especialidade	= cd_especialidade_p;
		end if;
	end if;
	
	cd_cbo_w	:= null;
	if (coalesce(nr_seq_cbo_saude_w,0) > 0) then
		select	cd_cbo
		into STRICT	cd_cbo_w
		from	cbo_saude
		where	nr_sequencia	= nr_seq_cbo_saude_w;
	end if;

elsif (coalesce(ie_versao_p,'0') <> '0') then --Com versão informada
	open c01;
	loop
	fetch c01 into
		nr_seq_cbo_saude_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */			
		nr_seq_cbo_saude_w := nr_seq_cbo_saude_w;
	end loop;
	close c01;

	if (coalesce(nr_seq_cbo_saude_w,0) = 0) then
		
		open c02;
		loop
		fetch c02 into
			nr_seq_cbo_saude_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			nr_seq_cbo_saude_w	:= nr_seq_cbo_saude_w;
		end loop;
		close c02;

		if (coalesce(nr_seq_cbo_saude_w,0) = 0) then
			
			open c03;
			loop
			fetch c03 into
				nr_seq_cbo_saude_w;
			EXIT WHEN NOT FOUND; /* apply on c03 */
				nr_seq_cbo_saude_w	:= nr_seq_cbo_saude_w;
			end loop;
			close c03;

				if (coalesce(nr_seq_cbo_saude_w,0) = 0) then
					select	coalesce(max(b.nr_seq_cbo_saude),0)
					into STRICT	nr_seq_cbo_saude_w
					from	pessoa_fisica b
					where	b.cd_pessoa_fisica	= cd_medico_p
					and	TISS_OBTER_SE_CBO_VERSAO(b.nr_seq_cbo_saude,ie_versao_p) = 'S';
					
					if (coalesce(nr_seq_cbo_saude_w,0) = 0) then	/*lhalves OS 369047 em 05/10/2011 - Buscar o CBO do cadastro da especialidade*/
			
						select	coalesce(max(b.nr_seq_cbo_saude),0)
						into STRICT	nr_seq_cbo_saude_w
						from	especialidade_medica b
						where	b.cd_especialidade	= cd_especialidade_p
						and	TISS_OBTER_SE_CBO_VERSAO(b.nr_seq_cbo_saude,ie_versao_p) = 'S';
					
					end if;
				end if;
		end if;

	end if;

	select	max(TISS_OBTER_VERSAO_CBOS(nr_seq_cbo_saude_w))
	into STRICT	ds_versao_w
	;
	
	cd_cbo_w	:= null;
	if (coalesce(nr_seq_cbo_saude_w,0) > 0) and (coalesce(ds_versao_w,'0') <> '0') then --Com versão informada
		select	cd_cbo
		into STRICT	cd_cbo_w
		from	cbo_saude
		where	nr_sequencia	= nr_seq_cbo_saude_w;
	end if;

end if;

return	cd_cbo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION tiss_obter_cbos_medico (cd_medico_p text, cd_especialidade_p bigint, ie_versao_p text, cd_convenio_p bigint) FROM PUBLIC;

