CREATE OR REPLACE FUNCTION tiss_obter_cbo_medico (cd_pessoa_fisica_p text) RETURNS varchar AS $body$
DECLARE


nr_seq_cbo_saude_w cbo_saude.nr_sequencia%type;
cd_cbo_w  cbo_saude.cd_cbo%type;


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then

 select coalesce(max(nr_seq_cbo_saude),0)
 into STRICT nr_seq_cbo_saude_w
 from pessoa_fisica
 where cd_pessoa_fisica = cd_pessoa_fisica_p;

 if (nr_seq_cbo_saude_w > 0) then

  select substr(cd_cbo,1,30)
  into STRICT cd_cbo_w
  from cbo_saude
  where nr_sequencia = nr_seq_cbo_saude_w;

 end if;

end if;

return cd_cbo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
