SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_cid_atendimento ( nr_atendimento_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


dt_diagnostico_w		timestamp;
ds_diagnostico_w		varchar(240);
ds_retorno_w		varchar(240);
cd_cid_w			varchar(240);


BEGIN

begin
select	max(dt_diagnostico)
into STRICT	dt_diagnostico_w
from	diagnostico_medico
where	nr_atendimento	= nr_atendimento_p;
exception
	when no_data_found then
		dt_diagnostico_w := null;
end;

if (dt_diagnostico_w IS NOT NULL AND dt_diagnostico_w::text <> '') then
	select	coalesce(max(ds_doenca_cid), null),
		coalesce(max(cd_doenca_cid), null)
	into STRICT	ds_diagnostico_w,
		cd_cid_w
	from 	cid_doenca
	where	cd_doenca_cid = (	SELECT	max(cd_doenca)
					from	diagnostico_doenca
					where	nr_atendimento	= nr_atendimento_p
					and	dt_diagnostico	= dt_diagnostico_w);
end if;

if (ie_opcao_p	= 'C') then
	ds_retorno_w	:= cd_cid_w;
elsif (ie_opcao_p	= 'D') then
	ds_retorno_w	:= ds_diagnostico_w;
end if;

return ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
