CREATE OR REPLACE FUNCTION tiss_obter_cid_mascara (cd_cid_p text) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(10);


BEGIN

if (cd_cid_p IS NOT NULL AND cd_cid_p::text <> '') and (length(cd_cid_p) = 4) then
	ds_retorno_w	:= substr(cd_cid_p,1,3) || '.' || substr(cd_cid_p,4,1);
else
	ds_retorno_w	:= cd_cid_p;
end if;

return ds_retorno_w;

end;
