CREATE OR REPLACE FUNCTION tiss_obter_codigo_simpro (cd_material_p bigint, cd_estabelecimento_p bigint, cd_convenio_p bigint, dt_referencia_p timestamp, nr_seq_marca_p bigint default 0, nr_seq_mat_simp_p material_atend_paciente.nr_seq_mat_simp%type default 0) RETURNS bigint AS $body$
DECLARE


/*
Edgar 09/09/2009, OS 165099, function criada a partir da "Obter_Codigo_Simpro", mas restringindo por cd_estabelecimento_p
*/
cd_simpro_w		bigint := null;
ie_tipo_convenio_w	smallint;

c01 CURSOR FOR
SELECT	cd_simpro
from	material_simpro
where	cd_material					= cd_material_p
and	coalesce(cd_estabelecimento,coalesce(cd_estabelecimento_p,0))	= coalesce(cd_estabelecimento_p,0)
and	coalesce(cd_convenio,coalesce(cd_convenio_p,0))		= coalesce(cd_convenio_p,0)
and	coalesce(ie_tipo_convenio, coalesce(ie_tipo_convenio_w,0)) 	= coalesce(ie_tipo_convenio_w,0)
and	coalesce(ie_situacao,'A')				= 'A'
and	coalesce(nr_seq_marca, coalesce(nr_seq_marca_p, 0))		= coalesce(nr_seq_marca_p, 0)
and	coalesce(dt_referencia_p,clock_timestamp()) between coalesce(dt_vigencia,to_date('01/01/1988','dd/mm/yyyy')) and trunc(coalesce(dt_final_vigencia,clock_timestamp())) + 89399/89400
order by coalesce(cd_convenio,0),
	coalesce(ie_tipo_convenio,0),
	coalesce(cd_estabelecimento,0),
	coalesce(nr_seq_marca,0),
	coalesce(dt_vigencia, clock_timestamp());


BEGIN

if (nr_seq_mat_simp_p IS NOT NULL AND nr_seq_mat_simp_p::text <> '') and coalesce(nr_seq_mat_simp_p, 0) > 0 then

	select	max(cd_simpro)
	into STRICT	cd_simpro_w
	from	material_simpro
	where	nr_sequencia = nr_seq_mat_simp_p;

else

	if (coalesce(cd_convenio_p,0) > 0) then
		select	coalesce(max(ie_tipo_convenio),0)
		into STRICT	ie_tipo_convenio_w
		from	convenio
		where	cd_convenio = cd_convenio_p;
	end if;

	open c01;
	loop
	fetch c01 into
		cd_simpro_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */	

	end loop;
	close c01;

	if (coalesce(cd_simpro_w::text, '') = '') then -- codigo SEM estalecimento	
		select	max(cd_simpro)
		into STRICT	cd_simpro_w
		from	material_simpro
		where	cd_material		= cd_material_p;
	end if;

end if;

return cd_simpro_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
