CREATE OR REPLACE FUNCTION tiss_obter_cod_brasindice ( cd_apresentacao_p text, cd_laboratorio_p text, cd_medicamento_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(15);
dt_inicio_vigencia_w	timestamp;

BEGIN

select	max(dt_inicio_vigencia)
into STRICT	dt_inicio_vigencia_w
from	brasindice_preco a
where	cd_medicamento		= cd_medicamento_p
and	cd_laboratorio		= cd_laboratorio_p
and	cd_apresentacao		= cd_apresentacao_p;

select	coalesce(max(cd_tiss),0)
into STRICT	ds_retorno_w
from	brasindice_preco a
where	cd_medicamento		= cd_medicamento_p
and	cd_laboratorio		= cd_laboratorio_p
and	cd_apresentacao		= cd_apresentacao_p
and	dt_inicio_vigencia	= dt_inicio_vigencia_w;

return ds_retorno_w;

end;
$body$
