CREATE OR REPLACE FUNCTION tiss_obter_cod_tuss_bras_vigen (cd_apresentacao_p text, cd_laboratorio_p text, cd_medicamento_p text, dt_vigencia_p timestamp, nr_seq_bras_preco_p bigint default null) RETURNS varchar AS $body$
DECLARE

ds_retorno_w		varchar(15);
dt_inicio_vigencia_w	timestamp;

BEGIN
if (coalesce(nr_seq_bras_preco_p::text, '') = '') then

	select	max(dt_inicio_vigencia)
	into STRICT	dt_inicio_vigencia_w
	from	brasindice_preco a
	where	cd_medicamento		= cd_medicamento_p
	and	cd_laboratorio		= cd_laboratorio_p
	and	cd_apresentacao		= cd_apresentacao_p;

	if (dt_vigencia_p IS NOT NULL AND dt_vigencia_p::text <> '') then
		dt_inicio_vigencia_w	:= dt_vigencia_p;
	end if;

	select	coalesce(max(cd_tuss),0)
	into STRICT	ds_retorno_w
	from	brasindice_preco a
	where	cd_medicamento		= cd_medicamento_p
	and	cd_laboratorio		= cd_laboratorio_p
	and	cd_apresentacao		= cd_apresentacao_p
	and	dt_inicio_vigencia	= dt_inicio_vigencia_w;
else
	select	coalesce(max(cd_tuss),0)
	into STRICT	ds_retorno_w
	from	brasindice_preco a
	where	nr_sequencia		= nr_seq_bras_preco_p;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
