SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_cod_usuario (cd_usuario_convenio_p text, ds_formato_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* Edgar 26/10/2007, OS 71630, function para formatar o codigo da carteira
ie_opcao_p
'0' - Numero da carteira
'D' - Digito verificador
'ND' - Numero de digitos sem o DV
*/
i				bigint;
ds_retorno_w			varchar(255);
ds_formato_w			varchar(255);
cd_usuario_convenio_w		varchar(255);
ds_retorno_aux_w		varchar(255);


BEGIN

ds_formato_w			:= '';
FOR 	i IN REVERSE length(ds_formato_p)..1 LOOP
	ds_formato_w		:= ds_formato_w || substr(ds_formato_p, i, 1);
end loop;

cd_usuario_convenio_w		:= '';
FOR 	i IN REVERSE length(coalesce(cd_usuario_convenio_p,0))..1 LOOP
	cd_usuario_convenio_w	:= cd_usuario_convenio_w || substr(cd_usuario_convenio_p, i, 1);
end loop;

FOR 	i IN 1..length(ds_formato_w) LOOP

	if (ie_opcao_p = '0') then
		if (substr(ds_formato_w, i, 1) = '0') then
			ds_retorno_w		:= ds_retorno_w || substr(cd_usuario_convenio_w, i, 1);
		end if;
	elsif (ie_opcao_p = 'D') then
		if (substr(ds_formato_w, i, 1) = 'D') then
			ds_retorno_w		:= ds_retorno_w || substr(cd_usuario_convenio_w, i, 1);
		end if;
	elsif (ie_opcao_p = 'ND') then
		if (substr(ds_formato_w, i, 1) = '0') then
			ds_retorno_w		:= ds_retorno_w || substr(cd_usuario_convenio_w, i, 1);
		end if;
	end if;


end loop;

ds_retorno_aux_w		:= '';
FOR 	i IN REVERSE length(coalesce(ds_retorno_w,0))..1 LOOP
	ds_retorno_aux_w		:= ds_retorno_aux_w || substr(ds_retorno_w, i, 1);
end loop;
ds_retorno_w			:= ds_retorno_aux_w;

if (ie_opcao_p = 'ND') then
	ds_retorno_w		:= to_char(length(ds_retorno_w));
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
