CREATE OR REPLACE FUNCTION tiss_obter_conversao_externa (nm_tabela_p text, nm_atributo_p text, cd_interno_p text) RETURNS varchar AS $body$
DECLARE


cd_externo_w			varchar(80);


BEGIN
cd_externo_w			:= '';
begin
select	cd_externo
into STRICT	cd_externo_w
from	conversao_meio_externo
where	coalesce(cd_cgc::text, '') = ''
and	nm_tabela	= nm_tabela_p
and	nm_atributo	= nm_atributo_p
and	cd_interno	= cd_interno_p;
exception
    	when others then
		cd_externo_w := '';
end;

if (coalesce(cd_externo_w::text, '') = '')	or (cd_externo_w = '')	then
	cd_externo_w	:= cd_interno_p;
end if;

RETURN cd_externo_w;

END;
$body$
