CREATE OR REPLACE FUNCTION tiss_obter_dados_marca (cd_estabelecimento_p bigint, cd_material_p bigint, nr_seq_marca_p bigint, cd_cgc_fornec_p text, nr_seq_lote_fornec_p bigint, nr_seq_material_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p

'RA' registro anvisa
'CF' código de referência
*/
ds_retorno_w			varchar(255)	:= null;
qt_reg_w			bigint;
cd_referencia_w			varchar(80);
nr_registro_anvisa_w		material_marca.nr_registro_anvisa%type;
cd_cgc_fornec_w			pessoa_juridica.cd_cgc%type;
nr_seq_marca_w			marca.nr_sequencia%type;

C01 CURSOR FOR
	SELECT	a.cd_referencia,
		a.nr_registro_anvisa
	from	material_marca a
	where (a.cd_estabelecimento	= cd_estabelecimento_p or coalesce(a.cd_estabelecimento::text, '') = '')
	and	a.cd_material		= cd_material_p
	and	((a.nr_sequencia	= nr_seq_marca_w) or (nr_seq_marca_w = 0))
	and	((a.cd_cnpj		= cd_cgc_fornec_w) or (coalesce(a.cd_cnpj::text, '') = ''))
	and	coalesce(a.ie_situacao,'A') 	= 'A'
	order by coalesce(a.qt_prioridade, 0);



BEGIN

if (cd_material_p IS NOT NULL AND cd_material_p::text <> '') then

	cd_cgc_fornec_w	:= coalesce(cd_cgc_fornec_p,'X');
	nr_seq_marca_w  := coalesce(nr_seq_marca_p,0);

	if (coalesce(nr_seq_lote_fornec_p,0) > 0) then

		select	coalesce(max(cd_cgc_fornec),cd_cgc_fornec_w),
			coalesce(max(nr_seq_marca),nr_seq_marca_w)
		into STRICT	cd_cgc_fornec_w,
			nr_seq_marca_w
		from	material_lote_fornec
		where	nr_sequencia = nr_seq_lote_fornec_p;
	elsif (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '') then

		select	coalesce(max(x.cd_fornec_consignado),cd_cgc_fornec_w),
			coalesce(max(x.nr_seq_marca),nr_seq_marca_w)
		into STRICT	cd_cgc_fornec_w,
			nr_seq_marca_w
		from	prescr_material x,
			material_atend_paciente a
		where	x.nr_sequencia = a.nr_sequencia_prescricao
		and	x.nr_prescricao = a.nr_prescricao
		and	a.nr_sequencia = nr_seq_material_p;

	end if;


	open C01;
	loop
	fetch C01 into
		cd_referencia_w,
		nr_registro_anvisa_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		cd_referencia_w 	:= cd_referencia_w;
		nr_registro_anvisa_w 	:= nr_registro_anvisa_w;
		end;
	end loop;
	close C01;


	if (ie_opcao_p = 'CF') then
		ds_retorno_w := cd_referencia_w;
	elsif (ie_opcao_p = 'RA') then


		if (coalesce(nr_registro_anvisa_w::text, '') = '') then

			select	max(nr_registro_anvisa)
			into STRICT	nr_registro_anvisa_w
			from	material_estab
			where	cd_material 		= cd_material_p
			and	cd_estabelecimento 	= cd_estabelecimento_p;

		end if;

		ds_retorno_w := nr_registro_anvisa_w;
	end if;
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
