CREATE OR REPLACE FUNCTION tiss_obter_dados_motivo_glosa (nr_seq_motivo_glosa_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w	varchar(255) := null;


BEGIN

if (coalesce(nr_seq_motivo_glosa_p,0) > 0) then

	if (ie_opcao_p = 'C') then

		select	cd_motivo_tiss
		into STRICT	ds_retorno_w
		from	tiss_motivo_glosa
		where	nr_sequencia = nr_seq_motivo_glosa_p;

	elsif (ie_opcao_p = 'D') then

		select	ds_motivo_tiss
		into STRICT	ds_retorno_w
		from	tiss_motivo_glosa
		where	nr_sequencia = nr_seq_motivo_glosa_p;

	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
