CREATE OR REPLACE FUNCTION tiss_obter_dados_prot_envio ( nr_seq_prot_envio_ret_p bigint, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE


/* ie_opcao_p
   'P'	- nº sequencia do protocolo convênio.
*/
ds_retorno_w		varchar(255);
nr_seq_protocolo_w	bigint;


BEGIN

select	max(nr_seq_protocolo)
into STRICT	nr_seq_protocolo_w
from	tiss_protocolo_envio_ret
where	nr_sequencia	= nr_seq_prot_envio_ret_p;

