SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_data_autor (nr_seq_autor_p bigint) RETURNS timestamp AS $body$
DECLARE


dt_autorizacao_w	timestamp;
dt_inicio_vigencia_w	timestamp;
dt_autor_conv_w		timestamp;
ie_dt_autor_sadt_w	varchar(10);
cd_estabelecimento_w	bigint;
cd_convenio_w		bigint;
nr_atendimento_w	bigint;
nr_seg_agenda_w		autorizacao_convenio.nr_seq_agenda%type;
nr_seq_agenda_consulta_w	autorizacao_convenio.nr_seq_agenda_consulta%type;


BEGIN



cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

select 	max(nr_atendimento),
	max(nr_seq_agenda),
	max(cd_convenio),
	max(dt_autorizacao),
	max(dt_inicio_vigencia),
	max(nr_seq_agenda_consulta),
	max(coalesce(cd_estabelecimento, cd_estabelecimento_w))
into STRICT	nr_atendimento_w,
	nr_seg_agenda_w,
	cd_convenio_w,
	dt_autor_conv_w,
	dt_inicio_vigencia_w,
	nr_seq_agenda_consulta_w,
	cd_estabelecimento_w
from 	autorizacao_convenio
where 	nr_sequencia		= nr_seq_autor_p;

if (nr_atendimento_w IS NOT NULL AND nr_atendimento_w::text <> '') then
	
	select 	max(cd_estabelecimento)
	into STRICT 	cd_estabelecimento_w
	from 	atendimento_paciente
	where	nr_atendimento	= nr_atendimento_w;

elsif (nr_seg_agenda_w IS NOT NULL AND nr_seg_agenda_w::text <> '') then
	
	select 	max(a.cd_estabelecimento)
	into STRICT	cd_estabelecimento_w
	from 	agenda a,
		agenda_paciente b
	where 	a.cd_agenda 	= b.cd_agenda
	and  	b.nr_sequencia 	= nr_seg_agenda_w;
	
elsif (nr_seq_agenda_consulta_w IS NOT NULL AND nr_seq_agenda_consulta_w::text <> '') then
	
	select 	max(a.cd_estabelecimento)
	into STRICT	cd_estabelecimento_w
	from 	agenda a,
		agenda_consulta b
	where 	a.cd_agenda 	= b.cd_agenda
	and  	b.nr_sequencia 	= nr_seq_agenda_consulta_w;
		
end if;
	
select	coalesce(max(IE_DT_AUTOR_SADT),'A')
into STRICT 	ie_dt_autor_sadt_w
from 	tiss_parametros_convenio
where 	cd_convenio 		= cd_convenio_w
and 	cd_estabelecimento 	= cd_estabelecimento_w;

if (ie_dt_autor_sadt_w = 'I') then
	dt_autorizacao_w 	:= dt_inicio_vigencia_w;
else
	dt_autorizacao_w 	:= dt_autor_conv_w;
end if;

return	dt_autorizacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION tiss_obter_data_autor (nr_seq_autor_p bigint) FROM PUBLIC;

