SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_desc_convenio ( cd_convenio_p bigint, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_desc_convenio_w	varchar(255);
ds_convenio_w		varchar(255);
cd_cgc_convenio_w	varchar(255);


BEGIN

select	coalesce(max(ie_desc_convenio),'C')
into STRICT	ie_desc_convenio_w
from	tiss_parametros_convenio
where	cd_estabelecimento	= cd_estabelecimento_p
and	cd_convenio		= cd_convenio_p;

if (ie_desc_convenio_w = 'C') then
	ds_convenio_w	:= obter_nome_convenio(cd_convenio_p);
elsif (ie_desc_convenio_w = 'R') then
	select	a.cd_cgc
	into STRICT	cd_cgc_convenio_w
	from	convenio a
	where	a.cd_convenio	= cd_convenio_p;

	select	substr(a.ds_razao_social,1,255)
	into STRICT	ds_convenio_w
	from	pessoa_juridica a
	where	a.cd_cgc	= cd_cgc_convenio_w;
end if;

return	ds_convenio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
