CREATE OR REPLACE FUNCTION tiss_obter_desc_face_dente (cd_face_dente_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Obter  e descriação da face do dente
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
ds_retorno_w
Retorna descrição face do dente, da tabela tiss_face_dente
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w		tiss_face_dente.ds_face%type	:= null;
ds_face_w		varchar(255);
cd_face_dente_w		varchar(1);
cd_faces_w		tiss_face_dente.cd_face%type;
i			integer	:= 1;


BEGIN
cd_faces_w	:= cd_face_dente_p;
while(cd_faces_w IS NOT NULL AND cd_faces_w::text <> '') loop
	cd_face_dente_w	:= substr(cd_faces_w,1,1);

	select	f.ds_face
	into STRICT	ds_face_w
	from	tiss_face_dente f
	where	f.cd_face = upper(cd_face_dente_w);

	if (coalesce(ds_retorno_w::text, '') = '') then
		ds_retorno_w	:= ds_face_w;
	else
		ds_retorno_w	:= ds_retorno_w	|| ', ' || ds_face_w;
	end if;

	i	:= i + 1;
	cd_faces_w	:= substr(cd_faces_w,i,length(cd_faces_w));
end loop;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
