CREATE OR REPLACE FUNCTION tiss_obter_desc_reg_boca (cd_regiao_p text) RETURNS varchar AS $body$
DECLARE


/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++
Finalidade:  Obter  e descriação da região da boca
-------------------------------------------------------------------------------------------------------------------
Locais de chamada direta:
[  ]  Objetos do dicionário [ X] Tasy (Delphi/Java) [  ] Portal [  ]  Relatórios [ ] Outros:
 ------------------------------------------------------------------------------------------------------------------
Pontos de atenção:
ds_retorno_w
Retorna descrição da região da boca, da tabela tiss_regiao_boca
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
ds_retorno_w	tiss_regiao_boca.ds_regiao%type;


BEGIN

if (cd_regiao_p IS NOT NULL AND cd_regiao_p::text <> '')	then
	select	b.ds_regiao
	into STRICT	ds_retorno_w
	from	tiss_regiao_boca b
	where	b.cd_regiao = upper(cd_regiao_p);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
