SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_doc_masc (nr_doc_convenio_p text, ds_mascara_p text) RETURNS varchar AS $body$
DECLARE


i			bigint;
ds_retorno_w		varchar(255)	:= null;
ds_numero_w		varchar(255);


BEGIN

ds_numero_w	:= somente_numero_char(ds_mascara_p);

if (ds_numero_w = ds_mascara_p) then

	select	trim(both to_char(somente_numero(nr_doc_convenio_p),ds_mascara_p))
	into STRICT	ds_retorno_w
	;

else

	FOR 	i IN 1..length(ds_mascara_p) LOOP
		if (substr(ds_mascara_p, i, 1) = 'S') then
			ds_retorno_w		:= ds_retorno_w || substr(nr_doc_convenio_p, i, 1);
		end if;
	end loop;

end if;

return	coalesce(ds_retorno_w, nr_doc_convenio_p);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
