CREATE OR REPLACE FUNCTION tiss_obter_dt_per_desp (ie_periodo_desp_p text, ie_tiss_tipo_despesa_p text, dt_referencia_p timestamp, ds_versao_p text) RETURNS timestamp AS $body$
DECLARE


dt_retorno_w	timestamp;

/* ie_periodo_desp_p
S 	Sempre
M 	Sempre maior horário
MED	Sempre maior horário (exceto Diárias)
MET	Sem maior horário (exceto Diárias e Taxas)
N	Não gerar
GTDA	Somente Gases/Taxas/Diárias/Aluguel
*/
BEGIN

dt_retorno_w		:= null;
if (coalesce(ie_periodo_desp_p, 'N') in ('S','M','MED','MET')) then
	dt_retorno_w	:= dt_referencia_p;
elsif (coalesce(ie_periodo_desp_p, 'N') = 'GTDA') then
	if	((ie_tiss_tipo_despesa_p in ('1','4','5','6')) and (obter_se_projeto_versao(0,12,ds_versao_p,0) = 'N')) or
		((ie_tiss_tipo_despesa_p in ('1','5','7')) and (obter_se_projeto_versao(0,12,ds_versao_p,0) = 'S')) then
		dt_retorno_w	:= dt_referencia_p;
	else
		dt_retorno_w	:= null;
	end if;
elsif (coalesce(ie_periodo_desp_p, 'N') = 'N') then
	dt_retorno_w	:= null;
end if;

return	dt_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 IMMUTABLE;
