CREATE OR REPLACE FUNCTION tiss_obter_espec_medico (cd_medico_p text, ie_opcao_p text) RETURNS varchar AS $body$
DECLARE

 
cd_especialidade_w		integer	:= null;
ds_especialidade_w		varchar(255)	:= '';

C01 CURSOR FOR 
SELECT	a.cd_especialidade 
from	medico_especialidade a 
where	a.cd_pessoa_fisica = cd_medico_p 
and	exists (SELECT	1 
		from	tiss_cbo_saude b 
		where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica 
		and	a.cd_especialidade	= b.cd_especialidade) 
order by a.nr_seq_prioridade desc;


BEGIN 
 
OPEN C01;
LOOP 
FETCH C01 into 
	cd_especialidade_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	cd_especialidade_w	:= cd_especialidade_w;
END LOOP;
CLOSE C01;
 
if (substr(ie_opcao_p,1,1) = 'D') and (cd_especialidade_w IS NOT NULL AND cd_especialidade_w::text <> '') then 
	select	ds_especialidade 
	into STRICT	ds_especialidade_w 
	from	especialidade_medica 
	where	cd_especialidade = cd_especialidade_w;
end if;
 
if (substr(ie_opcao_p,1,1) <> 'D') then 
	ds_especialidade_w	:= cd_especialidade_w;
end if;
 
RETURN ds_especialidade_w;
 
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
