SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION tiss_obter_estagio_autor (is_estatus_autor_p text) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w		bigint;
cd_empresa_w		empresa.cd_empresa%type;

BEGIN

select	coalesce(max(cd_empresa),0)
into STRICT	cd_empresa_w
from	estabelecimento
where	cd_estabelecimento = wheb_usuario_pck.get_cd_estabelecimento;

select	max(nr_sequencia)
into STRICT	nr_sequencia_w
from	estagio_autorizacao
where	coalesce(ie_situacao,'A')	= 'A'
and	ie_estagio_ret_autor	= is_estatus_autor_p
and	coalesce(cd_empresa,cd_empresa_w) = cd_empresa_w;

if (coalesce(nr_sequencia_w::text, '') = '') then

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	estagio_autorizacao
	where	coalesce(ie_situacao,'A') 	= 'A'
	and	ie_interno		= CASE WHEN is_estatus_autor_p='1' THEN  10 WHEN is_estatus_autor_p='2' THEN  5 WHEN is_estatus_autor_p='3' THEN  90 END
	and	coalesce(ie_parcial,'N') 	= 'N'
	and	coalesce(cd_empresa,cd_empresa_w) = cd_empresa_w;

end if;

return	nr_sequencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
